import{_ as t,c as d,a0 as o,o as l}from"./chunks/framework.Bakaa70P.js";const p=JSON.parse('{"title":"Webstorm","description":"","frontmatter":{},"headers":[],"relativePath":"tool/webstorm.md","filePath":"tool/webstorm.md","lastUpdated":1716256931000}'),c={name:"tool/webstorm.md"};function i(a,e,r,u,s,n){return l(),d("div",null,e[0]||(e[0]=[o('<h1 id="webstorm" tabindex="-1">Webstorm <a class="header-anchor" href="#webstorm" aria-label="Permalink to &quot;Webstorm&quot;">​</a></h1><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><ol><li><p>安装<code>2021.1</code>版本, 除了修改目录, 其它都直接下一步</p></li><li><p>启动, 选择试用<code>Evaluate for free</code>, 点击<code>Evaluate</code>进入IDE</p><ul><li>启动弹出报错 <code>java.util.concurrent.CompletionException: java.lang.IllegalStateException...</code></li><li>解决方式: <a href="./win#防火墙">防火墙</a></li></ul></li></ol><h2 id="配置" tabindex="-1">配置 <a class="header-anchor" href="#配置" aria-label="Permalink to &quot;配置&quot;">​</a></h2><ul><li><p>设置<code>Ctrl + Alt + S</code></p><ul><li>插件 -&gt; 已安装 -&gt; 取消所有勾选, 只保留 <code>Prettier</code>, <code>Vue.js</code>, <code>Markdown</code>, <code>EditorConfig</code>, <code>CSS</code>, <code>Sass</code></li><li>插件 -&gt; Marketplace -&gt; 搜索并安装 <ul><li><code>Wechat mini program support</code>, 支持微信小程序, uniapp (如格式化后不会分离数值和<code>rpx</code>) <ul><li>安装成功后, 确定 关闭设置窗口, 等待插件初始化, 再打开设置 -&gt; 语言和框架 -&gt; Wechat Mini Program <ul><li>小程序支持 -&gt; 勾选<code>启用</code></li></ul></li></ul></li><li><code>IDE Eval Reset</code>, 重置元数据 (针对<code>#WS-211.xxx</code>及更低版本) (或将<code>ide-eval-resetter-2.1.13.zip</code>拖拽到IDE) <ul><li>每隔30天, 菜单 -&gt; 帮助 -&gt; Eval Reset -&gt; Reset</li><li>如果超过30天, 那么就在弹窗中继续选择<code>Evaluate for free</code></li></ul></li></ul></li><li>外观和行为 -&gt; 主工具栏 <ul><li>移除 展开的所有项</li></ul></li></ul></li><li><p>文件 -&gt; 管理IDE设置</p><ul><li>导入设置: <code>settings.zip</code> (百度网盘)</li></ul></li></ul><h2 id="中文语言包" tabindex="-1">中文语言包 <a class="header-anchor" href="#中文语言包" aria-label="Permalink to &quot;中文语言包&quot;">​</a></h2><ul><li><p>菜单 -&gt; 帮助 -&gt; 关于</p><ul><li>内部版本号 #WS-211.xxx, <a href="https://marketplace.jetbrains.com.cn/files/13710/126017/zh.211.287.jar" target="_blank" rel="noreferrer">对应的语言包</a></li></ul></li><li><p><a href="https://plugins.jetbrains.com/plugin/13710-chinese-simplified-language-pack----/versions" target="_blank" rel="noreferrer">中文语言包历史版本</a></p><ul><li>Show More -&gt; 找到Version为<code>211</code>开头(匹配<code>内部版本号</code>), 然后<code>Download</code>, 下载好后, 拖拽到IDE中</li></ul></li></ul><h2 id="性能" tabindex="-1">性能 <a class="header-anchor" href="#性能" aria-label="Permalink to &quot;性能&quot;">​</a></h2><ul><li>设置 - 帮助 -&gt; 编辑自定义 VM 选项... <ul><li><code>-Xmx2048m</code> (电脑性能不是很高, 推荐1024即可)</li><li><code>-XX:ReservedCodeCacheSize=1024m</code></li></ul></li></ul><h2 id="快捷键-基于-sublime-text-键盘映射" tabindex="-1">快捷键 (基于 Sublime Text 键盘映射) <a class="header-anchor" href="#快捷键-基于-sublime-text-键盘映射" aria-label="Permalink to &quot;快捷键 (基于 Sublime Text 键盘映射)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>键名</th><th>键值</th><th>备注</th></tr></thead><tbody><tr><td>拆分并右移</td><td><code>Alt</code> + <code>Shift</code> + <code>2</code></td><td></td></tr><tr><td>向右拆分</td><td><code>Ctrl</code> + <code>Alt</code> + <code>2</code></td><td></td></tr><tr><td>取消拆分</td><td><code>Alt</code> + <code>X</code></td><td></td></tr><tr><td>重新打开关闭的选项卡</td><td><code>Alt</code> + <code>W</code></td><td></td></tr><tr><td>管理项目...</td><td><code>Ctrl</code> + <code>Shift</code> + <code>N</code></td><td>最近的项目</td></tr><tr><td>项目</td><td><code>Alt</code> + <code>1</code></td><td>切换显示左侧目录栏</td></tr><tr><td>npm</td><td><code>Alt</code> + <code>3</code></td><td>对应<code>package.json</code> -&gt; <code>scripts</code>序列项</td></tr><tr><td>服务</td><td><code>Alt</code> + <code>4</code></td><td>首次打开配置<code>添加服务</code></td></tr><tr><td>运行</td><td><code>Ctrl</code> + <code>Q</code></td><td>会自动打开<code>服务</code>, 并运行服务中的第一序列项</td></tr><tr><td>停止</td><td><code>Alt</code> + <code>Q</code></td><td></td></tr><tr><td>快速文档</td><td><code>Alt</code> + <code>E</code></td><td></td></tr><tr><td>跳转到源代码</td><td><code>Ctrl</code> + <code>Alt</code> + <code>E</code></td><td>链接可跳转到浏览器</td></tr><tr><td>随处搜索</td><td><code>Ctrl</code> + <code>Shift</code> + <code>Alt</code> + <code>F</code></td><td>搜索文件名</td></tr></tbody></table><h2 id="使用" tabindex="-1">使用 <a class="header-anchor" href="#使用" aria-label="Permalink to &quot;使用&quot;">​</a></h2><ul><li>导入项目 <ul><li>文件 -&gt; 打开 -&gt; 选择项目根目录 (<code>Ctrl</code> + <code>O</code>)</li></ul></li></ul><h2 id="配置eslint" tabindex="-1">配置ESLint <a class="header-anchor" href="#配置eslint" aria-label="Permalink to &quot;配置ESLint&quot;">​</a></h2><ul><li>设置 -&gt; 语言和框架 -&gt; JavaScript -&gt; 代码质量工具 -&gt; ESLint <ul><li>勾选 <code>自动 ESLint 配置</code></li><li>为文件运行 <code>src/**/*.{ts,vue} {ignore: **/*.d.ts}</code></li></ul></li></ul><h2 id="配置prettier" tabindex="-1">配置Prettier <a class="header-anchor" href="#配置prettier" aria-label="Permalink to &quot;配置Prettier&quot;">​</a></h2><ul><li>设置 <ul><li>插件 -&gt; 已安装 -&gt; Prettier</li><li>语言和框架 -&gt; JavaScript -&gt; Prettier <ul><li>为文件运行 <code>src/**/*.{ts,vue,scss}</code></li><li>勾选<code>代码重新格式化时</code></li></ul></li></ul></li></ul><h2 id="其它" tabindex="-1">其它 <a class="header-anchor" href="#其它" aria-label="Permalink to &quot;其它&quot;">​</a></h2><ul><li><p><code>typescript@4.9.5</code>之后版本无法识别<code>vue3</code>等类型</p></li><li><p>全局搜索不到</p><ul><li>文件 -&gt; 清除缓存 -&gt; 勾选所有 -&gt; 清除并重新启动</li></ul></li><li><p>启动报错: <code>failed to create a child event loop</code></p><ul><li>开启 <code>Windows Firewall (Windows防火墙服务)</code></li></ul></li><li><p>样式中根据实际警告提示, 添加 <code>swiper</code>, <code>uni-page-refresh</code> 等到html标签</p></li></ul>',19)]))}const g=t(c,[["render",i]]);export{p as __pageData,g as default};
